css:
.github-PushPullMenuView-selector {
  display: flex;
  align-items: center;
}
.github-PushPullMenuView-item {
  margin: 5px;
}
.github-PushPullMenuView-item.is-flexible {
  flex: 1;
}
.github-PushPullMenuView .icon-mark-github::before {
  margin-right: 0;
  color: #999;
}
.github-PushPullMenuView .btn-group {
  display: flex;
}
.github-PushPullMenuView .btn-group .btn {
  flex: 1;
}
.github-PushPullMenuView .btn-tooltip-wrapper .icon {
  pointer-events: none;
}
.github-PushPullMenuView-message {
  max-height: 4.8em;
  margin: 5px;
  padding: 5px;
  line-height: 1.4;
  white-space: normal;
  text-align: left;
  overflow-y: auto;
  border: 1px solid #9f9f9f;
  border-radius: 2px;
  background-color: #fff;
}
.github-PushPullMenuView-message:empty {
  display: none;
}
.github-PushPullMenuView.in-progress .icon-mark-github {
  position: relative;
  color: #444;
}
.github-PushPullMenuView.in-progress .icon-mark-github:before {
  color: inherit;
}
.github-PushPullMenuView.in-progress .icon-mark-github:after {
  content: "";
  position: absolute;
  top: -4px;
  left: -4px;
  width: 24px;
  height: 24px;
  border-top: 2px dotted;
  border-right: 2px dotted;
  border-bottom: 2px dotted transparent;
  border-radius: 16px;
  -webkit-animation: github-PushPullMenuView-animation 2s linear 12, github-PushPullMenuView-animation 4s 24s steps(12) 100;
}
@-webkit-keyframes github-PushPullMenuView-animation {
  100% {
    transform: rotate(360deg);
  }
}
